'use client'

import { useClerk } from '@clerk/nextjs';
import React from "react";
import { Logo } from "./Navbar";
import { useGlobalContext } from "@/app/context/GlobalContext";
import Link from "next/link";
import { useRouter } from 'next/navigation';

const Sidebar = () => {
  const {
    openSideBarObject: { openSideBar, setOpenSideBar },
  } = useGlobalContext();

  return (
    <div
      className={`${openSideBar ? "fixed z-50 shadow-lg" : "max-md:hidden"
        } max-md:w-[80%] md:w-[40%] xl:w-[20%] lg:w-[25%] bg-neutral-950 p-5 flex flex-col gap-2 h-screen pt-7 border-r border-neutral-700`}
    >
      <div className="flex items-center justify-center">
        <Logo />
      </div>
      <QuickLinks />
      <LanguagesLinks />
    </div>
  );
};

function QuickLinks() {

  const { signOut } = useClerk();
  const router = useRouter()

  const {
    sideBarMenuObject: { sideBarMenu, setSideBarMenu },
    openSideBarObject: { openSideBar, setOpenSideBar },
  } = useGlobalContext();

  const handleMenuClick = (index: number) => {
    const updatedSideBarMenu = sideBarMenu.map((menu, i) => {
      if (index === i) {
        return { ...menu, isSelected: true };
      } else {
        return { ...menu, isSelected: false };
      }
    });
    setSideBarMenu(updatedSideBarMenu);
  };


  const handleLogout = async () => {
    try {
      await signOut();
      setTimeout(() => router.push('/'), 100); // Small delay before navigation
    } catch (error) {
      console.error('Error during sign out:', error);
    }

  }

  return (
    <div className="ml-3 mt-14 text-md">
      <div className="font-bold text-slate-400">Quick Links</div>
      <ul className="text-slate-400 mt-4 flex flex-col gap-2">
        {sideBarMenu.map((menu, index) => {
          if (menu.name !== "Logout") {
            return (<Link key={index} href={menu.url}>
              <li
                key={index}
                onClick={() => handleMenuClick(index)}
                className={`flex gap-1 cursor-pointer select-none items-center p-[7px] px-2 rounded-md w-[60%] ${menu.isSelected ? "bg-yellow-500 text-black" : "text-gray-400"
                  } `}
              >
                <SVGIcon svgContent={`${menu.icons}`} />

                <span className="ml-2">{menu.name}</span>
              </li></Link>)
          }
          else {
            return (
              <li
                key={index}
                onClick={handleLogout}
                className={`flex gap-1 cursor-pointer select-none items-center p-[7px] px-2 rounded-md w-[60%] ${menu.isSelected ? "bg-yellow-500 text-black" : "text-gray-400"
                  } `}
              >
                <SVGIcon svgContent={`${menu.icons}`} />

                <span className="ml-2">{menu.name}</span>
              </li>
            )
          }
        })}
      </ul>
    </div>
  );
}

const SVGIcon = ({ svgContent }: { svgContent: string }) => (
  <div dangerouslySetInnerHTML={{ __html: svgContent }} className="w-6 h-6" />
);

